function Hd_cic = CIC
%CIC Returns a multirate filter object.

% MATLAB Code
% Generated by MATLAB(R) 9.10 and DSP System Toolbox 9.12.
% Generated on: 25-Oct-2025 12:59:12

decf    = 4;  % Decimation Factor
diffd   = 1;   % Differential Delay
numsecs = 2;   % Number of Sections

inputWL = 16;
extraBits = ceil(numsecs * log2(decf));           % worst-case growth in integer bits
secWordLen = inputWL + extraBits;

Hd_cic = dsp.CICDecimator( ...
    'FixedPointDataType', 'Specify word and fraction lengths', ...
    'DecimationFactor', decf, ...
    'DifferentialDelay', diffd, ...
    'NumSections', numsecs, ...
    'SectionWordLengths', repmat(secWordLen, 1, 2*numsecs), ...
    'SectionFractionLengths', repmat(15, 1, 2*numsecs), ...
    'OutputWordLength', 16, ...
    'OutputFractionLength', 15);

